package com.glh.glh.service;import com.glh.glh.dao.GlhDAO;import com.glh.glh.model.GlhModel;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Qualifier;import org.springframework.stereotype.Service;import com.sentence.elements.Nomens;import com.sentence.elements.Verbs;import com.sentence.elements.Words;import java.util.List;import java.util.Optional;import java.util.UUID;import java.util.Vector;@Servicepublic class GlhService {    private final GlhDAO glhDao;    @Autowired    public GlhService(@Qualifier("Dao") GlhDAO glhDao) {        this.glhDao = glhDao;    }    public String addWord(GlhModel glhModel){       return glhDao.insertWord(glhModel);    }    public Nomens getAllElements(){        return glhDao.selectWordElements();    }    public Verbs getVerbElements(){        return glhDao.selectVerbElements();    }    public Words getWordElements(){        return glhDao.showWordElements();    }}